CREATE TABLE `accounts` (  `objectId`     VARCHAR(36) NOT NULL,  `login`        VARCHAR(16) NOT NULL,  `password`     BLOB(32)    NOT NULL,  `accessLevel`  TINYINT     NULL,  `lastLogin`    DATETIME    NULL,  `lastIp`       VARCHAR(15) NULL,  `lastServerId` INT         NULL DEFAULT 0,  PRIMARY KEY (`objectId`, `login`));CREATE TABLE `accounts_log` (  `id`        INT         NOT NULL AUTO_INCREMENT,  `login`     VARCHAR(16) NOT NULL,  `operation` TINYINT     NULL,  `msgId`     SMALLINT    NULL,  `time`      DATETIME    NULL,  PRIMARY KEY (`id`));CREATE TABLE `accounts_settings` (  `id`    INT         NOT NULL AUTO_INCREMENT,  `login` VARCHAR(16) NOT NULL,  `key`   VARCHAR(64) NULL,  `value` VARCHAR(64) NULL,  PRIMARY KEY (`id`, `login`, `key`));ALTER TABLE `accounts_log` ADD FOREIGN KEY (`login`) REFERENCES `accounts` (`login`);ALTER TABLE `accounts_settings` ADD FOREIGN KEY (`login`) REFERENCES `accounts` (`login`);